$(function () {
	var audioPlayListVar = new jPlayerPlaylist({
			jPlayer: "#jquery_jplayer_1",
			cssSelectorAncestor: "#jp_container_1"
		}, audioData0, {
			swfPath: "http://www.news.cn/2015/txh2015/audio/js/",
			supplied: "m4v, mp3",
			wmode: "window",
			useStateClassSkin: true,
			autoBlur: false,
			smoothPlayBar: true,
			keyEnabled: false,
			remainingDuration: true,
			volume: 1.0,
			ready: function() {
				$(this).jPlayer("repeat");
				var $jpDetails = $('.jp-details');
				$jpDetails.show();
			}
		});
	//pause
	var   $audioPause = $("#audioPause")
	$audioPause.on("click", function () {
		audioPlayListVar.pause()
	});
});